import React, { useState, useEffect } from 'react';
import logo from './logo.svg';
import './App.css';
import axios from 'axios';


function App() {


  const defaultState = {
    tax: "123",
    subtotal:0.0,
    total:0.0,
    productName : "",
    price: 0.0,
    quantity: 0,
    itemList:[],
    location: "CA",
    isInit: true
  };

  const [state, setState] = useState({ ...defaultState });

  useEffect(() => {
    if(isInit)
      setState({ ...state, isInit:false });
  });

  const {
    tax,
    subtotal,
    total,
    price,
    productName,
    quantity,
    itemList,
    location
  } = state;

  function purchase(){
    let initConfig = {
      headers: {
        "Content-Type": "application/json",
        "Cache-Control": "no-cache,no-store,must-revalidate,max-age=-1,private",
        "Access-Control-Allow-Origin": "*",
        "Access-Control-Allow-Methods": "GET,PUT,POST,DELETE,PATCH,OPTIONS",
        Expires: "-1"
      }
    };
    const instance = axios.create(initConfig);

    let path = "http://localhost:8080/v1/purchase";

    let payload = {
      itemList : state.itemList,
      location : state.location
    }

    instance.post(path,payload).then(
      response => {
        console.log(response);
      },
      error => console.log(error)
    )
  };

  function add(productName,price,quantity){
    let tempItemList = [];
    tempItemList.push({
      productName: productName,
      price : price,
      quantity : quantity
    })
    console.log(state)
  }

  function handleChange(e){
    setState({
      [e.target.name] : e.target.value
    })
  }

    return (
      <div className="App">       
        <div className="App-header">
          <p>
            Product:   
            <br/>
            <input name="productName" onChange={handleChange}/>
          </p>
          <p>
            Price:
            <br/>
            <input name="price" onChange={handleChange}/>
          </p>
          <p>
            Quantity
            <br/>
            <input name="quantity" onChange={handleChange}/>
          </p>
          <p>
            <select name="location" onChange={handleChange}>
              <option value="CA">California</option>
              <option value="NY">New York</option>
            </select>
            <br/>
          </p>
          <button onClick={() => add({productName},{price},{quantity})}>Add</button>
          <br/>
          <button onClick={() => purchase()}>Purchase</button>
        </div>
        <div>
          {itemList}
        </div>
      </div>
    )
}

export default App;
